import React, { Component } from 'react';
import Header from '../HeaderComponent/Header';
import Search from '../SearchComponent/Search';
import employees from '../../utils/employee.json';

export default class EmployeeContainer extends Component {
  state = {
    employees,
    deptSearch: [],
    emailSearch: []
  };

  handleDeptSearch = (event) => {
    event.preventDefault();
    console.log(event.target.name);
  };

  handleEmailSearch = (event) => {
    let value = event.target.value;
    const employees = this.state.employees

    let emailSearch = employees.filter(employee => employee.email.toLowerCase().includes(value.toLowerCase()));

    this.setState({
        ...this.state, emailSearch
    }, console.log("State", this.state))
  };

  render() {
    return (
      <div>
        <Header />
        <Search 
        handleEmailSearch={this.handleEmailSearch}
        handleDeptSearch={this.handleDeptSearch}
        // handleFormSubmit={this.handleFormSubmit}
        />
        <div className='jumbotron'>
          <h3>List of Employees</h3>
          <table className='table'>
            <thead className='thead-dark'>
              <tr>
                <th scope='col'>Pic</th>
                <th scope='col'>FirstName</th>
                <th scope='col'>LastName</th>
                <th scope='col'>Email</th>
                <th scope='col'>PhoneNumber</th>
                <th scope='col'>Department</th>
                <th scope='col'>Manager</th>
              </tr>
            </thead>
            <tbody>
              {this.state.employees.map((employee) => (
                <tr>
                  <td>
                    <img alt={employee.firstName} src={employee.picture} />
                  </td>
                  <td>{employee.firstName}</td>
                  <td>{employee.lastName}</td>
                  <td>{employee.email}</td>
                  <td>{employee.phone}</td>
                  <td>{employee.department}</td>
                  <td>{employee.manager}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    );
  }
}
