import React, { Component } from 'react';
import Header from '../HeaderComponent/Header';
import Search from '../SearchComponent/Search';

export default class EmployeeContainer extends Component {
  state = {
    Picure: '1',
    FirstName: 'Ankit',
    LastName: 'Mudvari',
    Email: 'ankmud01@gmail.com',
    PhoneNumber: '120-456-7891',
    Department: 'Information Technology',
    Manager: 'John Kim',
  };

  customSort = (sortName, sortOrder, data) => {
    var order = sortOrder === 'desc' ? -1 : 1;
    data.sort(function (a, b) {
      var aa = +(a[sortName] + '').replace(/[^\d]/g, '');
      var bb = +(b[sortName] + '').replace(/[^\d]/g, '');
      if (aa < bb) {
        return order * -1;
      }
      if (aa > bb) {
        return order;
      }
      return 0;
    });
  };

  render() {
    return (
      <div>
        <Header />
        <Search />
        <div className='jumbotron'>
          <h3>List of Employees</h3>
          <table
            id='table'
            data-toggle='table'
            data-height='460'
            data-custom-sort='customSort'
            data-url='json/data1.json'
          >
            <thead>
              <tr>
                <th data-field='id' data-sortable='true'>
                  ID
                </th>
                <th data-field='name' data-sortable='true'>
                  First Name
                </th>
                <th data-field='price' data-sortable='true'>
                  Last Name
                </th>
                <th data-field='price' data-sortable='false'>
                  Email
                </th>
                <th data-field='price' data-sortable='false'>
                  Phone Number
                </th>
                <th data-field='price' data-sortable='false'>
                  Department
                </th>
                <th data-field='price' data-sortable='false'>
                  Manager
                </th>
              </tr>
            </thead>
          </table>
          <table class='table'>
            <thead class='thead-dark'>
              <tr>
                <th scope='col'>Pic</th>
                <th scope='col'>FirstName</th>
                <th scope='col'>LastName</th>
                <th scope='col'>Email</th>
                <th scope='col'>PhoneNumber</th>
                <th scope='col'>Department</th>
                <th scope='col'>Manager</th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <td>{this.state.Picure}</td>
                <td>{this.state.FirstName}</td>
                <td>{this.state.LastName}</td>
                <td>{this.state.Email}</td>
                <td>{this.state.PhoneNumber}</td>
                <td>{this.state.Department}</td>
                <td>{this.state.Manager}</td>
              </tr>
            </tbody>
          </table>
        </div>
      </div>
    );
  }
}
